tables.table_schema:
CREATE TABLE land_use_scenarios (
    id INT PRIMARY KEY,
    area VARCHAR(50) NOT NULL,
    land_type VARCHAR(50) NOT NULL,
    population INT NOT NULL
);

CREATE TABLE historical_data (
    id INT PRIMARY KEY,
    year INT NOT NULL,
    land_area DECIMAL NOT NULL,
    population INT NOT NULL
);

CREATE TABLE historical_data (
  location_id INT,
  land_use_category VARCHAR(50),
  year INT,
  area_ha FLOAT
);

CREATE TABLE predictive_model (
  model_id INT,
  model_name VARCHAR(50),
  accuracy FLOAT,
  trained_on_date TIMESTAMP
);

CREATE TABLE historical_data (
    id INT PRIMARY KEY,
    land_type TEXT,
    land_area FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE model_parameters (
    id INT PRIMARY KEY,
    parameter_name TEXT,
    parameter_value FLOAT,
    model_id INT
);

CREATE TABLE predicted_land_use (
    id INT PRIMARY KEY,
    land_type TEXT,
    land_area_predicted FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE actual_land_use (
    id INT PRIMARY KEY,
    land_type TEXT,
    actual_area FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE model_results (
    id INT PRIMARY KEY,
    model_id INT,
    prediction_accuracy FLOAT
);
