tables.table_schema:
CREATE TABLE housing_affordability_indexes (
	number_of_bedrooms INT,
	neighborhood VARCHAR,
	average_price INT,
	affordability_index FLOAT
);

CREATE TABLE demographic_data (
	neighborhood VARCHAR,
	population INT,
	median_income INT
);

CREATE TABLE Neighborhoods (
    neighborhood_id INT PRIMARY KEY,
    neighborhood_name VARCHAR(50),
    city VARCHAR(50),
    state VARCHAR(2)
);

CREATE TABLE neighborhood_affordability (
    neighborhood_id INT PRIMARY KEY,
    neighborhood_name VARCHAR(255),
    affordability_index FLOAT
);

CREATE TABLE housing_prices (
    house_id INT PRIMARY KEY,
    neighborhood_id INT,
    house_price FLOAT,
    house_size INT
);

CREATE TABLE population_data (
    city_id INT PRIMARY KEY,
    neighborhood_id INT,
    total_population INT,
    median_age FLOAT
);

CREATE TABLE amenities (
    amenity_id INT PRIMARY KEY,
    neighborhood_id INT,
    amenity_name VARCHAR(255),
    amenity_type VARCHAR(50)
);

CREATE TABLE HousingAffordabilityIndex (
	nh_id INT PRIMARY KEY,
	nh_name VARCHAR(50),
	median_income FLOAT,
	housing_price_index FLOAT
);

CREATE TABLE NeighborhoodPopulation (
	nh_id INT PRIMARY KEY,
	population INT,
	overall_area FLOAT,
	population_density FLOAT
);

CREATE TABLE HousingInventory (
	house_id INT PRIMARY KEY,
	nh_id INT,
	num_bedrooms INT,
	square_footage FLOAT
);

CREATE TABLE HousingCosts (
	house_id INT PRIMARY KEY,
	nh_id INT,
	monthly_rent FLOAT,
	monthly_mortgage FLOAT
);

CREATE TABLE AffordabilityRating (
	nh_id INT PRIMARY KEY,
	housing_affordability_score FLOAT,
	rating VARCHAR(10)
);
