tables.table_schema:
CREATE TABLE regions (
  region_id INT PRIMARY KEY,
  region_name VARCHAR(50),
  population_size INT,
  population_density FLOAT
);

CREATE TABLE regions (
  region_id INT PRIMARY KEY,
  region_name VARCHAR(255),
  population INT,
  area DOUBLE
);

CREATE TABLE population_density (
  region_id INT REFERENCES regions(region_id),
  density DOUBLE,
  median_income INT,
  CONSTRAINT pk_population_density PRIMARY KEY (region_id)
);

CREATE TABLE spatial_data (
  region_id INT REFERENCES regions(region_id),
  location GEOMETRY,
  CONSTRAINT fk_spatial_data FOREIGN KEY (region_id) REFERENCES regions(region_id)
);

CREATE TABLE regions (
  region_id INT PRIMARY KEY,
  region_name VARCHAR(255) NOT NULL,
  population_density FLOAT
);

CREATE TABLE cities (
  city_id INT PRIMARY KEY,
  city_name VARCHAR(255) NOT NULL,
  region_id INT,
  population INT
);

CREATE TABLE countries (
  country_id INT PRIMARY KEY,
  country_name VARCHAR(255) NOT NULL
);

CREATE TABLE region_cities (
  region_id INT,
  city_id INT,
  PRIMARY KEY (region_id, city_id)
);

CREATE TABLE regions(
    region_id INT PRIMARY KEY,
    region_name TEXT,
    population INT
);

CREATE TABLE population_density(
    density_id INT PRIMARY KEY,
    region_id INT,
    density_value FLOAT,
    FOREIGN KEY (region_id) REFERENCES regions(region_id)
);

CREATE TABLE spatial_data(
    data_id INT PRIMARY KEY,
    region_id INT,
    spatial_info GEOMETRY,
    FOREIGN KEY (region_id) REFERENCES regions(region_id)
);

CREATE TABLE demographics(
    demographics_id INT PRIMARY KEY,
    region_id INT,
    age_group TEXT,
    population_size INT,
    FOREIGN KEY (region_id) REFERENCES regions(region_id)
);

CREATE TABLE region_coordinates(
    region_id INT PRIMARY KEY,
    latitude FLOAT,
    longitude FLOAT,
    FOREIGN KEY (region_id) REFERENCES regions(region_id)
);
