tables.table_schema:
CREATE TABLE air_pollution_data (
    id INT PRIMARY KEY,
    location VARCHAR(255),
    pollution_level INT
);

CREATE TABLE monitoring_stations (
    station_id INT PRIMARY KEY,
    station_name VARCHAR(255),
    latitude DECIMAL,
    longitude DECIMAL
);

CREATE TABLE AirPollutionMonitorings (
  monitor_id INT PRIMARY KEY,
  latitude FLOAT,
  longitude FLOAT,
  pollution_level INT
);

CREATE TABLE HeatmapData (
  map_id INT PRIMARY KEY,
  monitor_id INT,
  date DATE,
  heatmap_image BYTEA
);

CREATE TABLE AreaDetails (
  area_id INT PRIMARY KEY,
  area_name VARCHAR(50),
  high_concentration BOOLEAN,
  description TEXT
);

CREATE TABLE HeatmapData (
   heatmap_id INT PRIMARY KEY,
   coordinates JSON,
   pollution_level FLOAT
);

CREATE TABLE HeatmapSettings (
   setting_id INT PRIMARY KEY,
   threshold_value FLOAT,
   color_scheme VARCHAR(50)
);

CREATE TABLE Monitoring_Stations (
    station_id INT PRIMARY KEY,
    station_name VARCHAR(50) NOT NULL,
    location_lat DECIMAL NOT NULL,
    location_long DECIMAL NOT NULL
);

CREATE TABLE Pollution_Criteria (
    criteria_id INT PRIMARY KEY,
    min_pollution_level INT NOT NULL,
    max_pollution_level INT NOT NULL
);

CREATE TABLE Heatmap_Areas (
    area_id INT PRIMARY KEY,
    area_name VARCHAR(50) NOT NULL,
    heatmap_data JSON NOT NULL
);

CREATE TABLE Heatmap_Images (
    image_id INT PRIMARY KEY,
    area_id INT NOT NULL,
    image_url VARCHAR(255) NOT NULL,
    FOREIGN KEY (area_id) REFERENCES Heatmap_Areas(area_id)
);
