tables.table_schema:
CREATE TABLE Wildlife_Populations (
	population_id SERIAL PRIMARY KEY,
	population_name VARCHAR,
	location VARCHAR
);

CREATE TABLE Animal_Details (
   animal_id INT PRIMARY KEY,
   species_name VARCHAR(100),
   habitat VARCHAR(100),
   population_size INT
);

CREATE TABLE Habitat_Details (
   habitat_id INT PRIMARY KEY,
   habitat_name VARCHAR(100),
   terrain_type VARCHAR(50),
   climate VARCHAR(50)
);

CREATE TABLE Movement_Patterns (
   tracking_id INT,
   movement_type VARCHAR(50),
   speed FLOAT,
   direction VARCHAR(50)
);

CREATE TABLE species (
    species_id INT PRIMARY KEY,
    species_name VARCHAR(255) NOT NULL
);

CREATE TABLE tracking_devices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(255) NOT NULL,
    device_type VARCHAR(50) NOT NULL
);

CREATE TABLE animal_population (
    population_id INT PRIMARY KEY,
    species_id INT NOT NULL,
    population_size INT NOT NULL,
    location VARCHAR(255) NOT NULL
);
