tables.table_schema:
CREATE TABLE areas_prone_to_flooding (
  area_id INT PRIMARY KEY,
  area_name VARCHAR(255) NOT NULL,
  elevation FLOAT NOT NULL
);

CREATE TABLE land_features (
  feature_id INT PRIMARY KEY,
  feature_name VARCHAR(255) NOT NULL,
  elevation FLOAT NOT NULL
);

CREATE TABLE areas (
    area_id INT PRIMARY KEY,
    area_name VARCHAR(50) NOT NULL,
    elevation FLOAT NOT NULL
);

CREATE TABLE land_features (
    feature_id INT PRIMARY KEY,
    feature_name VARCHAR(50) NOT NULL,
    area_id INT REFERENCES areas(area_id),
    elevation FLOAT NOT NULL
);

CREATE TABLE flooding_events (
    event_id INT PRIMARY KEY,
    event_name VARCHAR(50) NOT NULL,
    area_id INT REFERENCES areas(area_id),
    date DATE NOT NULL
);

CREATE TABLE elevation_data (
  id SERIAL PRIMARY KEY,
  location_name TEXT NOT NULL,
  elevation FLOAT NOT NULL
);

CREATE TABLE flood_reports (
  report_id SERIAL PRIMARY KEY,
  location_id INT NOT NULL,
  report_date DATE NOT NULL,
  flood_level INT NOT NULL
);

CREATE TABLE flood_predictions (
  prediction_id SERIAL PRIMARY KEY,
  location_id INT NOT NULL,
  prediction_date DATE NOT NULL,
  flood_probability FLOAT NOT NULL
);

CREATE TABLE affected_areas (
  area_id SERIAL PRIMARY KEY,
  location_id INT NOT NULL,
  area_name TEXT NOT NULL,
  affected_population INT NOT NULL
);
