tables.table_schema:
CREATE TABLE satellite_images (
    image_id SERIAL PRIMARY KEY,
    image_name TEXT NOT NULL,
    image_date DATE NOT NULL,
    image_data BYTEA
);

CREATE TABLE spatial_data (
    spatial_id SERIAL PRIMARY KEY,
    spatial_coordinates POINT NOT NULL,
    vegetation_coverage FLOAT NOT NULL
);

CREATE TABLE image_metadata (
    metadata_id SERIAL PRIMARY KEY,
    image_id INT REFERENCES satellite_images(image_id),
    spatial_id INT REFERENCES spatial_data(spatial_id),
    capture_time TIMESTAMP
);

CREATE TABLE vegetation_changes (
    change_id SERIAL PRIMARY KEY,
    image_id INT REFERENCES satellite_images(image_id),
    change_description TEXT,
    change_date DATE
);

CREATE TABLE vegetation_cover (
    date DATE,
    satellite_image_id INT,
    vegetation_index FLOAT
);

CREATE TABLE satellite_images (
    satellite_image_id INT,
    image_url VARCHAR,
    acquisition_date DATE
);

CREATE TABLE spatial_data (
    location_point VARCHAR,
    satellite_image_id INT
);

CREATE TABLE change_detection (
    change_id INT,
    date DATE,
    satellite_image_id1 INT,
    satellite_image_id2 INT
);

CREATE TABLE vegetation_types (
    vegetation_type_id INT,
    vegetation_type_name VARCHAR
);
