CREATE TABLE Stops (
    stop_id INT PRIMARY KEY,
    stop_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL,
    time_estimated TIME NOT NULL
);

CREATE TABLE Vehicles (
    vehicle_id INT PRIMARY KEY,
