tables.table_schema:
CREATE TABLE satellites (
  satellite_id INT PRIMARY KEY,
  satellite_name VARCHAR(255),
  launch_date DATE
);

CREATE TABLE satellite_images (
    image_id UUID PRIMARY KEY,
    image_name VARCHAR(255) NOT NULL,
    timestamp TIMESTAMP NOT NULL,
    location GEOMETRY NOT NULL
);

CREATE TABLE deforestation_changes (
    change_id UUID PRIMARY KEY,
    image_id UUID NOT NULL,
    change_type VARCHAR(50) NOT NULL,
    area_effected FLOAT NOT NULL
);

CREATE TABLE satellite_metadata (
    image_id UUID PRIMARY KEY,
    satellite_name VARCHAR(100) NOT NULL,
    resolution INT NOT NULL,
    sensor_type VARCHAR(50) NOT NULL
);

CREATE TABLE satellite_tracks (
    track_id UUID PRIMARY KEY,
    image_id UUID NOT NULL,
    track_data JSONB NOT NULL
);

CREATE TABLE SatelliteImages (
  id INT PRIMARY KEY,
  image_name VARCHAR(255),
  image_data BYTEA,
  timestamp TIMESTAMP
);

CREATE TABLE DeforestationAreas (
  id INT PRIMARY KEY,
  area_name VARCHAR(255),
  area_data GEOMETRY,
  timestamp TIMESTAMP
);

CREATE TABLE SatelliteImageTags (
  id INT PRIMARY KEY,
  tag_name VARCHAR(255),
  image_id INT,
  FOREIGN KEY (image_id) REFERENCES SatelliteImages(id)
);

CREATE TABLE ChangeLogs (
  id INT PRIMARY KEY,
  change_details TEXT,
  timestamp TIMESTAMP
);
