tables.table_schema:
CREATE TABLE LandCoverResults (
    result_id INT PRIMARY KEY,
    image_id INT,
    classification VARCHAR(50),
    confidence FLOAT
);
CREATE TABLE RemoteSensingData (
    image_id INT PRIMARY KEY,
    image_path VARCHAR(255),
    capture_date DATE,
    sensor_type VARCHAR(50)
);

CREATE TABLE land_cover_results (
  id SERIAL PRIMARY KEY,
  latitude FLOAT,
  longitude FLOAT,
  classification_result VARCHAR
);

CREATE TABLE sensor_data (
  sensor_id SERIAL PRIMARY KEY,
  sensor_name VARCHAR,
  sensor_type VARCHAR
);

CREATE TABLE analysis_results (
  analysis_id SERIAL PRIMARY KEY,
  result_description TEXT,
  accuracy FLOAT
);

CREATE TABLE land_cover_types (
    id UUID PRIMARY KEY,
    type_name STRING NOT NULL,
    description STRING NOT NULL
);

CREATE TABLE land_cover_classes (
    class_id INT PRIMARY KEY,
    class_name TEXT
);

CREATE TABLE land_cover_results (
    result_id INT PRIMARY KEY,
    class_id INT,
    coverage_area FLOAT,
    result_date DATE
);

CREATE TABLE remote_sensing_data (
    data_id INT PRIMARY KEY,
    location_name TEXT,
    acquisition_date DATE,
    data_file BYTEA
);

CREATE TABLE analysis_results (
    analysis_id INT PRIMARY KEY,
    result_id INT,
    analysis_date DATE,
    analysis_summary TEXT
);

CREATE TABLE data_sources (
    source_id INT PRIMARY KEY,
    source_name TEXT,
    data_type TEXT,
    source_url TEXT
);
