tables.table_schema:
CREATE TABLE sensor_metadata (
  sensor_id INT PRIMARY KEY,
  sensor_name VARCHAR(255) NOT NULL,
  acquisition_date DATE NOT NULL,
  location VARCHAR(255)
);

CREATE TABLE sensor_data (
  sensor_id INT PRIMARY KEY,
  sensor_name VARCHAR(255),
  acquisition_date DATE
);

CREATE TABLE acquisition_sites (
  site_id INT PRIMARY KEY,
  site_name VARCHAR(255),
  site_location VARCHAR(255)
);

CREATE TABLE sensors (
    sensor_id SERIAL PRIMARY KEY,
    sensor_name VARCHAR(50) NOT NULL,
    location TEXT,
    date_acquired DATE
);

CREATE TABLE acquisition_dates (
    acquisition_id SERIAL PRIMARY KEY,
    sensor_id INT REFERENCES sensors(sensor_id),
    acquisition_date DATE
);

CREATE TABLE sensor_metadata (
    metadata_id SERIAL PRIMARY KEY,
    sensor_id INT REFERENCES sensors(sensor_id),
    metadata_description TEXT
);

CREATE TABLE sensor_information (
    sensor_id INT PRIMARY KEY,
    sensor_name VARCHAR(50),
    acquisition_date DATE
);

CREATE TABLE sensor_data (
    data_id INT PRIMARY KEY,
    sensor_id INT,
    data_value FLOAT,
    data_timestamp TIMESTAMP
);

CREATE TABLE sensor_location (
    location_id INT PRIMARY KEY,
    sensor_id INT,
    latitude FLOAT,
    longitude FLOAT
);

CREATE TABLE sensor_metadata (
    metadata_id INT PRIMARY KEY,
    sensor_id INT,
    metadata_type VARCHAR(50),
    metadata_value TEXT
);
