tables.table_schema:
CREATE TABLE spectral_signatures (
    signature_id SERIAL PRIMARY KEY,
    land_cover_type VARCHAR(50) NOT NULL,
    spectral_values JSONB NOT NULL
);

CREATE TABLE land_cover_types (
    type_id SERIAL PRIMARY KEY,
    land_cover_name VARCHAR(50) UNIQUE NOT NULL
);

CREATE TABLE remote_sensing_data (
    data_id SERIAL PRIMARY KEY,
    capture_date DATE NOT NULL,
    signature_id INT REFERENCES spectral_signatures(signature_id),
    type_id INT REFERENCES land_cover_types(type_id)
);

CREATE TABLE spectral_signatures_land_cover_types (
    id INT PRIMARY KEY,
    land_cover_type VARCHAR(50),
    spectral_wave_length_range VARCHAR(50),
    spectral_intensity FLOAT
);

CREATE TABLE land_cover_categories (
    id INT PRIMARY KEY,
    land_cover_category VARCHAR(50),
    description TEXT
);

CREATE TABLE data_sources (
    id INT PRIMARY KEY,
    data_source_name VARCHAR(50),
    provider VARCHAR(50),
    coverage_area TEXT
);
