tables.table_schema:
CREATE TABLE hyperspectral_data (
    id INT PRIMARY KEY,
    spectral_data JSONB,
    collection_date TIMESTAMP,
    sensor_id INT
);

CREATE TABLE research_facilities (
    facility_id SERIAL PRIMARY KEY,
    facility_name VARCHAR(100),
    location VARCHAR(100),
    researcher_id INT
);

CREATE TABLE hyperspectral_data (
    wavelength_range VARCHAR(50),
    wavelength_values FLOAT[],
    sensor_type VARCHAR(50),
    spatial_resolution FLOAT
);

CREATE TABLE hyperspectral_data (
    id INT PRIMARY KEY,
    wavelength_range FLOAT,
    measurement_data FLOAT,
    collection_date TIMESTAMP
);

CREATE TABLE sensing_platform (
    platform_id INT PRIMARY KEY,
    platform_name VARCHAR(50),
    platform_type VARCHAR(50)
);

CREATE TABLE research_partners (
    partner_id INT PRIMARY KEY,
    partner_name VARCHAR(50),
    institution VARCHAR(100)
);

CREATE TABLE remote_sensing_platform (
    platform_id INT PRIMARY KEY,
    platform_name VARCHAR(50),
    location_geocode VARCHAR(20),
    technician_id INT
);

CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(50),
    affiliation VARCHAR(100),
    email VARCHAR(50)
);

CREATE TABLE data_analysis_results (
    result_id INT PRIMARY KEY,
    project_id INT,
    data_processed TEXT,
    analysis_date DATE
);
