tables.table_schema:
CREATE TABLE satellite_images (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    file_path VARCHAR(100),
    image_data BYTEA
);

CREATE TABLE vegetation_indices (
    id INT PRIMARY KEY,
    pixel_x INT,
    pixel_y INT,
    ndvi FLOAT
);

CREATE TABLE satellite_images (
    image_id INT PRIMARY KEY,
    capture_date DATE,
    image_data BYTEA
);

CREATE TABLE image_processing_log (
    log_id SERIAL PRIMARY KEY,
    process_date TIMESTAMP,
    success BOOLEAN
);
