tables.table_schema:
CREATE TABLE lidar_data (
id INT,
latitude FLOAT,
longitude FLOAT,
elevation FLOAT
);

CREATE TABLE topographic_analysis (
id INT,
area_name VARCHAR(255),
elevation_model VARCHAR(255),
slope FLOAT
);

CREATE TABLE LiDAR_data (
    id INT PRIMARY KEY,
    point_cloud_data BYTEA,
    elevation DOUBLE PRECISION,
    intensity DOUBLE PRECISION
);

CREATE TABLE Terrain_Model (
    id INT PRIMARY KEY,
    terrain_name VARCHAR(255),
    terrain_type VARCHAR(100)
);

CREATE TABLE Remote_Sensing_Project (
    project_id UUID,
    project_name VARCHAR(255),
    client_name VARCHAR(255),
    project_status VARCHAR(50)
);

CREATE TABLE Project_LiDAR_Association (
    project_id UUID,
    lidar_table_id INT,
    PRIMARY KEY (project_id, lidar_table_id)
);
