tables.table_schema:
CREATE TABLE image_processing_algorithms (
    algorithm_id INT PRIMARY KEY,
    algorithm_name VARCHAR(50),
    parameters JSONB,
    created_at TIMESTAMP
);

CREATE TABLE ImageProcessingAlgorithms (
    algorithm_id INT PRIMARY KEY,
    algorithm_name VARCHAR(255),
    algorithm_description TEXT
);

CREATE TABLE image_algorithms (
    algorithm_id UUID PRIMARY KEY,
    algorithm_name VARCHAR(255) NOT NULL,
    parameters JSONB
);

CREATE TABLE algorithm_parameters (
    parameter_id UUID PRIMARY KEY,
    algorithm_id UUID REFERENCES image_algorithms(algorithm_id),
    parameter_name VARCHAR(255) NOT NULL,
    parameter_value VARCHAR(255) NOT NULL
);

CREATE TABLE remote_sensing_data (
    data_id UUID PRIMARY KEY,
    image_path VARCHAR(255) NOT NULL,
    algorithm_id UUID REFERENCES image_algorithms(algorithm_id),
    parameter_id UUID REFERENCES algorithm_parameters(parameter_id)
);

CREATE TABLE image_processing_algorithms (
    algorithm_id INT PRIMARY KEY,
    algorithm_name VARCHAR(255),
    algorithm_description TEXT
);

CREATE TABLE image_algorithms (
    algorithm_id INT PRIMARY KEY,
    algorithm_name VARCHAR(255),
    algorithm_description TEXT
);

CREATE TABLE remote_sensing_data (
    data_id INT PRIMARY KEY,
    date_processed DATE,
    satellite_name VARCHAR(100),
    image_url TEXT
);
