tables.table_schema:
CREATE TABLE ClimatePatterns (
    pattern_id INT PRIMARY KEY,
    pattern_description TEXT,
    start_date DATE,
    end_date DATE
);

CREATE TABLE Satellite_Information ( 
  satellite_id INT PRIMARY KEY, 
  satellite_name VARCHAR(50), 
  launch_date DATE, 
  orbit_type VARCHAR(20) 
);

CREATE TABLE satellites (
    id INT PRIMARY KEY,
    satellite_name VARCHAR,
    launch_date DATE,
    orbit_type VARCHAR
);

CREATE TABLE data_sources (
    source_id INT PRIMARY KEY,
    source_name VARCHAR,
    source_type VARCHAR
);
