tables.table_schema:
CREATE TABLE traffic_data (
  id INT PRIMARY KEY,
  location VARCHAR(255),
  congestion_level INT,
  timestamp TIMESTAMP
);

CREATE TABLE congestion_forecast (
  id INT PRIMARY KEY,
  location VARCHAR(255),
  forecasted_level INT,
  forecast_timestamp TIMESTAMP
);

CREATE TABLE congestion_data (
  id INT,
  location VARCHAR,
  congestion_level INT,
  timestamp TIMESTAMP
);

CREATE TABLE traffic_cameras (
  camera_id INT,
  location VARCHAR,
  camera_type VARCHAR,
  status VARCHAR
);

CREATE TABLE road_segments (
  segment_id INT,
  start_location VARCHAR,
  end_location VARCHAR,
  road_type VARCHAR
);

CREATE TABLE streets (
  street_id INT PRIMARY KEY,
  street_name VARCHAR(255) NOT NULL,
  city_name VARCHAR(255) NOT NULL,
  traffic_level INT
);

CREATE TABLE traffic_lights (
  light_id INT PRIMARY KEY,
  street_id INT NOT NULL,
  light_color VARCHAR(50) NOT NULL,
  status VARCHAR(50) NOT NULL
);

CREATE TABLE traffic_cameras (
  camera_id INT PRIMARY KEY,
  street_id INT NOT NULL,
  camera_location VARCHAR(255) NOT NULL,
  camera_status VARCHAR(50) NOT NULL
);

CREATE TABLE traffic_reports (
  report_id INT PRIMARY KEY,
  camera_id INT NOT NULL,
  report_date DATE NOT NULL,
  congestion_level INT
);

CREATE TABLE traffic_predictions (
  prediction_id INT PRIMARY KEY,
  report_id INT NOT NULL,
  prediction_date DATE NOT NULL,
  predicted_congestion_level INT
);
