tables.table_schema:
CREATE TABLE biodiversity_hotspots (
  id INT PRIMARY KEY,
  name TEXT,
  location GEOGRAPHY
);

CREATE TABLE hotspot_locations (
    id INT PRIMARY KEY,
    location_name VARCHAR(255),
    coordinates GEOMETRY
);

CREATE TABLE species_info (
    species_id INT PRIMARY KEY,
    species_name VARCHAR(255),
    habitat VARCHAR(255),
    population_density INT
);

CREATE TABLE biodiversity_hotspots (
    id INT PRIMARY KEY,
    hotspot_name VARCHAR(255),
    location GEOGRAPHY,
    biodiversity_index FLOAT
);

CREATE TABLE species_data (
    species_id INT PRIMARY KEY,
    species_name VARCHAR(255),
    habitat_type VARCHAR(50),
    abundance INT
);

CREATE TABLE BiodiversityHotspots (
    id INT PRIMARY KEY,
    name VARCHAR(100),
    location GEOGRAPHY,
    species_count INT
);
