tables.table_schema:
CREATE TABLE Land_Use_Data (
    id INT PRIMARY KEY,
    area DECIMAL,
    land_type VARCHAR(255),
    sequestration_rate DECIMAL
);

CREATE TABLE carbon_sequestration_rates (
    land_use_id INT PRIMARY KEY,
    land_use_category VARCHAR(50),
    sequestration_rate DECIMAL
);

CREATE TABLE land_use_data (
    data_id INT PRIMARY KEY,
    land_use_id INT,
    area_in_hectares DECIMAL,
    carbon_content DECIMAL
);

CREATE TABLE land_use_categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(50),
    description TEXT
);

CREATE TABLE carbon_sequestration (
  land_use_category VARCHAR(255),
  area_in_acres FLOAT,
  carbon_sequestration_rate FLOAT
);

CREATE TABLE forest_data (
  forest_name VARCHAR(255),
  tree_species VARCHAR(255),
  tree_age INT,
  tree_height FLOAT
);

CREATE TABLE agricultural_data (
  farm_name VARCHAR(255),
  crop_type VARCHAR(255),
  acreage FLOAT,
  carbon_content_per_acre FLOAT
);

CREATE TABLE urban_data (
  city_name VARCHAR(255),
  population INT,
  green_spaces_area FLOAT,
  carbon_offset_rate FLOAT
);

CREATE TABLE CarbonSequestrationRates (
  id INT PRIMARY KEY,
  land_use_type VARCHAR(255),
  sequestration_rate DECIMAL
);

CREATE TABLE LandUseData (
  id INT PRIMARY KEY,
  land_type VARCHAR(255),
  area DECIMAL
);

CREATE TABLE SequestrationFactors (
  id INT PRIMARY KEY,
  land_use_type VARCHAR(255),
  factor DECIMAL
);

CREATE TABLE EmissionsData (
  id INT PRIMARY KEY,
  land_use_type VARCHAR(255),
  emissions DECIMAL
);

CREATE TABLE CarbonCredits (
  id INT PRIMARY KEY,
  land_use_type VARCHAR(255),
  credits DECIMAL
);
