tables.table_schema:
CREATE TABLE HabitatFragmentation (
  year INT,
  land_cover_type VARCHAR(50),
  area_covered_km2 FLOAT
);

CREATE TABLE habitats (
   habitat_id INT PRIMARY KEY,
   habitat_name VARCHAR(255) NOT NULL,
   habitat_type VARCHAR(50) NOT NULL
);

CREATE TABLE land_cover_data (
   data_id INT PRIMARY KEY,
   habitat_id INT REFERENCES habitats(habitat_id),
   land_cover_type VARCHAR(50) NOT NULL,
   land_cover_date DATE
);

CREATE TABLE habitat_fragmentation (
    year INT,
    land_cover_code INT,
    land_cover_label VARCHAR(50)
);

CREATE TABLE land_cover_codes (
    land_cover_code INT PRIMARY KEY,
    land_cover_description VARCHAR(100)
);

CREATE TABLE habitat_changes (
    change_id SERIAL PRIMARY KEY,
    year INT,
    area_affected FLOAT,
    change_type VARCHAR(50)
);

CREATE TABLE habitat_fragmentation (
  time_period TIMESTAMP,
  area_covered DECIMAL,
  fragmentation_level INT
);

CREATE TABLE land_cover (
  land_type VARCHAR,
  coverage DECIMAL,
  area_covered DECIMAL
);

CREATE TABLE fragmentation_history (
  time_period TIMESTAMP,
  fragment_size INT,
  fragment_density DECIMAL
);

CREATE TABLE fragmentation_factors (
  factor_name VARCHAR,
  impact_level INT
);
