tables.table_schema:
CREATE TABLE climate_change_impacts (
    impact_id INT PRIMARY KEY,
    distribution_id INT NOT NULL,
    impact_description TEXT NOT NULL,
    impact_date DATE
);

CREATE TABLE species (
    species_id INT,
    species_name VARCHAR(255),
    species_type VARCHAR(50),
    species_population INT
);

CREATE TABLE climate_data (
    data_id INT,
    data_date DATE,
    temperature_mean FLOAT,
    precipitation_mean FLOAT
);

CREATE TABLE species_distribution (
    distribution_id INT,
    species_id INT,
    distribution_region VARCHAR(50),
    distribution_probability FLOAT
);
