tables.table_schema:
CREATE TABLE population_data (
    id INT PRIMARY KEY,
    species_name VARCHAR(255),
    population_size INT,
    conservation_status VARCHAR(50)
);

CREATE TABLE conservation_records (
    record_id INT PRIMARY KEY,
    observation_date DATE,
    location VARCHAR(255),
    conservation_success BOOLEAN
);

CREATE TABLE conservation_agency (
    agency_id INT PRIMARY KEY,
    agency_name VARCHAR(255) NOT NULL,
    contact_info VARCHAR(255)
);

CREATE TABLE species_population (
    species_id INT PRIMARY KEY,
    species_name VARCHAR(255) NOT NULL,
    population_size INT NOT NULL,
    conservation_status VARCHAR(50) NOT NULL
);

CREATE TABLE population (
    id SERIAL PRIMARY KEY,
    species VARCHAR(50) NOT NULL,
    population_count INT NOT NULL,
    conservation_status VARCHAR(50) NOT NULL
);
CREATE TABLE location (
    id SERIAL PRIMARY KEY,
    region VARCHAR(50) NOT NULL,
    latitude FLOAT NOT NULL,
    longitude FLOAT NOT NULL
);
CREATE TABLE conservation_organization (
    id SERIAL PRIMARY KEY,
    org_name VARCHAR(100) NOT NULL,
    location_id INT NOT NULL,
    start_date DATE NOT NULL,
    budget DECIMAL(12, 2) NOT NULL
);
CREATE TABLE observation (
    id SERIAL PRIMARY KEY,
    observation_date DATE NOT NULL,
    location_id INT NOT NULL,
    population_id INT NOT NULL,
    observer_name VARCHAR(100) NOT NULL
);

CREATE TABLE Species (
  species_id INT PRIMARY KEY,
  species_name VARCHAR(50) NOT NULL,
  habitat_type VARCHAR(50) NOT NULL
);

CREATE TABLE Researcher (
  researcher_id INT PRIMARY KEY,
  researcher_name VARCHAR(50) NOT NULL,
  researcher_email VARCHAR(50) UNIQUE NOT NULL
);
