tables.table_schema:
CREATE TABLE deforestation_biodiversity (
  spatial_data GEOMETRY,
  biodiversity_index FLOAT,
  deforestation_rate FLOAT
);

CREATE TABLE DeforestationEffects (
    id INT PRIMARY KEY,
    region VARCHAR(50),
    deforestation_area DECIMAL,
    biodiversity_index DECIMAL
);

CREATE TABLE SpatialData (
    id INT PRIMARY KEY,
    region VARCHAR(50),
    latitude DECIMAL,
    longitude DECIMAL
);

CREATE TABLE deforestation_data (
    id SERIAL PRIMARY KEY,
    region TEXT,
    deforestation_rate DECIMAL
);

CREATE TABLE spatial_data (
    id SERIAL PRIMARY KEY,
    location_name TEXT,
    area DECIMAL,
    geom GEOMETRY
);
