tables.table_schema:
CREATE TABLE ImpactAssessment (
  assessment_id INT PRIMARY KEY,
  commercial_development VARCHAR(255),
  impact_on_small_businesses VARCHAR(255),
  assessment_date DATE
);

CREATE TABLE SmallBusinesses (
  business_id INT PRIMARY KEY,
  business_name VARCHAR(255) NOT NULL,
  business_type VARCHAR(100) NOT NULL,
  business_location VARCHAR(255) NOT NULL
);

CREATE TABLE CommercialDevelopments (
  development_id INT PRIMARY KEY,
  development_name VARCHAR(255) NOT NULL,
  development_type VARCHAR(100) NOT NULL,
  development_location VARCHAR(255) NOT NULL
);

CREATE TABLE BusinessOwners ( 
     business_owner_id INT PRIMARY KEY, 
     business_name TEXT NOT NULL, 
     business_type TEXT NOT NULL, 
     business_location TEXT NOT NULL 
 );
