tables.table_schema:
CREATE TABLE locations (
    location_id SERIAL PRIMARY KEY,
    latitude DECIMAL,
    longitude DECIMAL
);

CREATE TABLE distances (
    distance_id SERIAL PRIMARY KEY,
    location1_id INTEGER REFERENCES locations(location_id),
    location2_id INTEGER REFERENCES locations(location_id),
    distance DECIMAL
);

CREATE TABLE location (
    id INT PRIMARY KEY,
    latitude FLOAT,
    longitude FLOAT
);

CREATE TABLE user (
    id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE
);

CREATE TABLE point_a (
  id INT PRIMARY KEY,
  latitude FLOAT,
  longitude FLOAT
);

CREATE TABLE point_b (
  id INT PRIMARY KEY,
  latitude FLOAT,
  longitude FLOAT
);

CREATE TABLE distance (
  id INT PRIMARY KEY,
  point_a_id INT,
  point_b_id INT,
  calculated_distance FLOAT
);

CREATE TABLE geolocation (
  id INT PRIMARY KEY,
  city VARCHAR(50),
  latitude FLOAT,
  longitude FLOAT
);
