tables.table_schema:
CREATE TABLE endangered_species (
	species_id INT PRIMARY KEY,
	species_name TEXT,
	habitat_id INT,
	location GEOMETRY
);

CREATE TABLE habitats (
	habitat_id INT PRIMARY KEY,
	habitat_name TEXT,
	habitat_type TEXT,
	region GEOMETRY
);

CREATE TABLE habitats (
    habitat_id INT PRIMARY KEY,
    habitat_name VARCHAR(50),
    location_geometry GEOGRAPHY,
    habitat_description TEXT
);

CREATE TABLE species_distribution (
    species_id INT,
    habitat_id INT,
    CONSTRAINT fk_species FOREIGN KEY (species_id) REFERENCES endangered_species(species_id),
    CONSTRAINT fk_habitat FOREIGN KEY (habitat_id) REFERENCES habitats(habitat_id)
);

CREATE TABLE species (
	species_id INT PRIMARY KEY,
	species_name TEXT NOT NULL,
	habitat_id INT,
	CONSTRAINT habitat_fk FOREIGN KEY (habitat_id) REFERENCES habitats(habitat_id)
);

CREATE TABLE habitats (
	habitat_id INT PRIMARY KEY,
	habitat_name TEXT NOT NULL
);

CREATE TABLE species (
	species_id INT PRIMARY KEY,
	species_name VARCHAR(50),
	habitat_id INT
);

CREATE TABLE habitats (
	habitat_id INT PRIMARY KEY,
	habitat_name VARCHAR(50),
	habitat_location GEOMETRY
);

CREATE TABLE endangered_species (
	species_id INT,
	endangerment_level VARCHAR(20)
);

CREATE TABLE species_habitats (
	species_id INT,
	habitat_id INT
);

CREATE TABLE habitat_distribution (
	habitat_id INT,
	distribution_area GEOMETRY
);
