tables.table_schema:
CREATE TABLE spatial_indexes (
    id INT PRIMARY KEY,
    location GEOGRAPHY
);

CREATE TABLE spatial_data (
    id SERIAL PRIMARY KEY,
    latitude FLOAT,
    longitude FLOAT,
    location GEOGRAPHY
);

CREATE TABLE spatial_index (
    id SERIAL PRIMARY KEY,
    data_id INT,
    spatial_index GEOMETRY,
    CONSTRAINT fk_data_id FOREIGN KEY (data_id) REFERENCES spatial_data(id)
);

CREATE TABLE locations (
    location_id INT PRIMARY KEY,
    name TEXT,
    coordinates GEOGRAPHY
);

CREATE TABLE cities (
    city_id INT PRIMARY KEY,
    name TEXT,
    population INT,
    area DECIMAL
);

CREATE TABLE countries (
    country_id INT PRIMARY KEY,
    name TEXT,
    population INT,
    area DECIMAL
);

CREATE TABLE distances (
    distance_id INT PRIMARY KEY,
    source_location_id INT,
    destination_location_id INT,
    distance DECIMAL
);

CREATE TABLE spatial_index (
    index_id INT PRIMARY KEY,
    index_name TEXT,
    indexed_table_name TEXT,
    index_type TEXT
);
