tables.table_schema:
CREATE TABLE traffic_flow (
  id SERIAL PRIMARY KEY,
  location VARCHAR(255),
  flow_level INT,
  congestion_level INT
);

CREATE TABLE map_settings (
  id SERIAL PRIMARY KEY,
  map_type VARCHAR(255),
  zoom_level INT,
  show_traffic BOOLEAN
);

CREATE TABLE traffic_monitoring (
    id INT NOT NULL,
    location VARCHAR(255) NOT NULL,
    traffic_level INT NOT NULL,
    congestion_level INT NOT NULL
);

CREATE TABLE traffic_cameras (
    camera_id INT NOT NULL,
    location VARCHAR(255) NOT NULL,
    camera_type VARCHAR(50) NOT NULL,
    status VARCHAR(20) NOT NULL
);

CREATE TABLE traffic_events (
    event_id INT NOT NULL,
    event_type VARCHAR(50) NOT NULL,
    event_description TEXT,
    reported_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE TrafficFlow (
    id INT PRIMARY KEY,
    location_id INT,
    congestion_level INT,
    timestamp TIMESTAMP
);

CREATE TABLE Locations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(255)
);

CREATE TABLE Maps (
    map_id INT PRIMARY KEY,
    map_name VARCHAR(255),
    map_data JSONB
);

CREATE TABLE Sensors (
    sensor_id INT PRIMARY KEY,
    location_id INT,
    sensor_type VARCHAR(50)
);

CREATE TABLE TrafficCameras (
    camera_id INT PRIMARY KEY,
    location VARCHAR(255),
    longitude DECIMAL(10, 6),
    latitude DECIMAL(10, 6)
);

CREATE TABLE MapOverlay (
    overlay_id INT PRIMARY KEY,
    overlay_name VARCHAR(100),
    overlay_data JSON
);
