tables.table_schema:
CREATE TABLE user_engagement_metrics (
    user_id INT PRIMARY KEY,
    engagement_score FLOAT,
    last_activity_date TIMESTAMP,
    subscription_status BOOLEAN
);

CREATE TABLE user_engagement_metrics (
    user_id INT PRIMARY KEY,
    last_login TIMESTAMP,
    total_sessions INT,
    avg_session_duration INT
);

CREATE TABLE purchase_history (
    user_id INT PRIMARY KEY,
    total_purchase_amt DECIMAL(10, 2),
    last_purchase_date TIMESTAMP,
    num_purchases INT
);

CREATE TABLE churn_prediction (
    user_id INT PRIMARY KEY,
    prediction_score DECIMAL(5, 2),
    prediction_date TIMESTAMP
);

CREATE TABLE user_activities (
    activity_id UUID primary key,
    user_id UUID references users(user_id),
    activity_type VARCHAR(50),
    activity_date TIMESTAMP
);

CREATE TABLE user_engagement (
    engagement_id UUID primary key,
    user_id UUID references users(user_id),
    engagement_score INT,
    engagement_date TIMESTAMP
);

CREATE TABLE churn_predictions (
    prediction_id UUID primary key,
    user_id UUID references users(user_id),
    prediction_date TIMESTAMP,
    churn_probability DECIMAL(5,2)
);

CREATE TABLE churn_reasons (
    reason_id UUID primary key,
    user_id UUID references users(user_id),
    reason_text VARCHAR(255),
    reason_date TIMESTAMP
);
