tables.table_schema:
CREATE TABLE social_media_users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    followers_count INT,
    following_count INT
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    join_date DATE
);

CREATE TABLE posts (
    post_id INT PRIMARY KEY,
    user_id INT,
    post_content TEXT,
    post_date DATE
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    join_date DATE
);

CREATE TABLE posts (
    post_id INT PRIMARY KEY,
    user_id INT REFERENCES users(user_id),
    post_content TEXT,
    post_date TIMESTAMP
);

CREATE TABLE likes (
    like_id INT PRIMARY KEY,
    user_id INT REFERENCES users(user_id),
    post_id INT REFERENCES posts(post_id),
    like_date TIMESTAMP
);

CREATE TABLE sentiment_analysis (
    analysis_id INT PRIMARY KEY,
    post_id INT REFERENCES posts(post_id),
    analysis_result VARCHAR(50),
    analysis_date TIMESTAMP
);
