tables.table_schema:
CREATE TABLE interaction_trends (
    trend_id INT PRIMARY KEY,
    trend_name VARCHAR(50),
    trend_description TEXT,
    trend_timeframe DATE
);

CREATE TABLE support_agents ( 
   agent_id UUID PRIMARY KEY, 
   agent_name VARCHAR(50), 
   agent_email VARCHAR(50) UNIQUE, 
   agent_role VARCHAR(50) 
 );

CREATE TABLE interaction_feedback ( 
   feedback_id UUID PRIMARY KEY, 
   interaction_id UUID, 
   feedback_score INT, 
   feedback_comment TEXT 
 );

CREATE TABLE support_agents (
    agent_id UUID PRIMARY KEY,
    agent_name VARCHAR(100) NOT NULL,
    agent_email VARCHAR(100) UNIQUE
);

CREATE TABLE support_issues (
    issue_id UUID PRIMARY KEY,
    interaction_id UUID NOT NULL,
    issue_type VARCHAR(50) NOT NULL,
    issue_description TEXT
);

CREATE TABLE agents (
    agent_id INT PRIMARY KEY,
    agent_name VARCHAR(50),
    agent_email VARCHAR(50),
    agent_department VARCHAR(50)
);

CREATE TABLE feedback (
    feedback_id INT PRIMARY KEY,
    interaction_id INT,
    rating INT,
    feedback_desc TEXT
);
