tables.table_schema:
CREATE TABLE customer_satisfaction_ratings (
	customer_id INT,
	rating INT,
	feedback TEXT
);

CREATE TABLE CustomerSatisfaction (
    customer_id INT PRIMARY KEY,
    satisfaction_rating INT,
    comments TEXT
);

CREATE TABLE AreasForImprovement (
    improvement_id INT PRIMARY KEY,
    customer_id INT,
    improvement_area TEXT,
    FOREIGN KEY (customer_id) REFERENCES CustomerSatisfaction(customer_id)
);

CREATE TABLE Customer_satisfaction_ratings (
    customer_id INT PRIMARY KEY,
    rating FLOAT NOT NULL,
    feedback TEXT
);

CREATE TABLE Customer_feedback (
    feedback_id INT PRIMARY KEY,
    customer_id INT NOT NULL,
    feedback_text TEXT,
    feedback_date TIMESTAMP
);

CREATE TABLE Customer_details (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50) NOT NULL,
    customer_email VARCHAR(100) UNIQUE,
    customer_phone VARCHAR(15)
);

CREATE TABLE products (
    product_id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    price DECIMAL(10,2),
    category VARCHAR(50)
);

CREATE TABLE departments (
    department_id INT PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    manager_id INT,
    location VARCHAR(255)
);
