tables.table_schema:
CREATE TABLE referral_links (
  referral_id INT PRIMARY KEY,
  link VARCHAR(255) NOT NULL,
  clicks INT DEFAULT 0,
  shares INT DEFAULT 0
);

CREATE TABLE click_tracking (
  click_id INT PRIMARY KEY,
  referral_id INT REFERENCES referral_links(referral_id),
  timestamp TIMESTAMP,
  user_id INT
);

CREATE TABLE links (
    link_id INT PRIMARY KEY,
    referral_source VARCHAR(255),
    click_count INT,
    share_count INT
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(255),
    email VARCHAR(255)
);

CREATE TABLE clicks (
    click_id INT PRIMARY KEY,
    link_id INT,
    user_id INT,
    timestamp TIMESTAMP
);

CREATE TABLE social_media_referrals (
  referral_id UUID PRIMARY KEY,
  link_clicks INT,
  shares INT,
  referral_source VARCHAR(255)
);

CREATE TABLE users (
  user_id UUID PRIMARY KEY,
  username VARCHAR(50) NOT NULL,
  email VARCHAR(255) UNIQUE
);

CREATE TABLE clicks (
  click_id UUID PRIMARY KEY,
  referral_id UUID,
  click_time TIMESTAMP,
  user_id UUID
);

CREATE TABLE shares (
  share_id UUID PRIMARY KEY,
  referral_id UUID,
  share_time TIMESTAMP,
  user_id UUID
);

CREATE TABLE link_clicks (
    id BIGSERIAL PRIMARY KEY,
    referral_id BIGINT NOT NULL,
    user_id BIGINT NOT NULL,
    click_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE link_shares (
    id BIGSERIAL PRIMARY KEY,
    referral_id BIGINT NOT NULL,
    user_id BIGINT NOT NULL,
    share_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE referrals (
    id BIGSERIAL PRIMARY KEY,
    referral_url VARCHAR(255) NOT NULL,
    website_id BIGINT NOT NULL
);

CREATE TABLE users (
    id BIGSERIAL PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(255) NOT NULL
);

CREATE TABLE websites (
    id BIGSERIAL PRIMARY KEY,
    website_name VARCHAR(100) NOT NULL,
    domain_name VARCHAR(100) NOT NULL
);
