tables.table_schema:
CREATE TABLE SentimentAnalysis (
    comment_id INT PRIMARY KEY,
    comment_text TEXT,
    tone_score FLOAT
);

CREATE TABLE ToneAnalysis (
    tone_id INT PRIMARY KEY,
    tone_name VARCHAR(50),
    analysis_date TIMESTAMP,
    comment_id INT REFERENCES SentimentAnalysis(comment_id)
);

CREATE TABLE SentimentAnalysis (
    id INT PRIMARY KEY,
    comment TEXT,
    tone VARCHAR(50)
);

CREATE TABLE Commenters (
    commenter_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(50)
);

CREATE TABLE AnalysisResults (
    result_id INT PRIMARY KEY,
    comment_id INT,
    sentiment_score FLOAT,
    analyzed_at TIMESTAMP
);

CREATE TABLE Comments (
    id INT PRIMARY KEY,
    comment_text TEXT,
    sentiment_score FLOAT
);

CREATE TABLE Messages (
    id INT PRIMARY KEY,
    message_text TEXT,
    sentiment_score FLOAT
);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    username TEXT
);

CREATE TABLE comments (
    comment_id INT PRIMARY KEY,
    user_id INT,
    comment_text TEXT,
    sentiment_score INT
);

CREATE TABLE messages (
    message_id INT PRIMARY KEY,
    user_id INT,
    message_text TEXT,
    sentiment_score INT
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username TEXT,
    email TEXT
);

CREATE TABLE sentiment_analysis (
    analysis_id INT PRIMARY KEY,
    text_type TEXT,
    total_comments INT,
    average_sentiment_score FLOAT
);

CREATE TABLE analysis_results (
    result_id INT PRIMARY KEY,
    analysis_id INT,
    comment_id INT,
    sentiment_score INT
);
