);

CREATE TABLE patients (
 patient_id INT PRIMARY KEY,
 patient_name VARCHAR(50) NOT NULL,
 department_id INT NOT NULL,
 FOREIGN KEY (department_id) REFERENCES departments(department_id)
);

CREATE TABLE visits (
 visit_id INT PRIMARY KEY,
 patient_id INT NOT NULL,
 visit_date DATE NOT NULL,
 FOREIGN KEY (patient_id) REFERENCES patients(patient_id)
);

CREATE TABLE stay_length (
 patient_id INT PRIMARY KEY,
 length_of_stay INT NOT NULL,
 FOREIGN KEY (patient_id) REFERENCES patients(patient_id)
);

CREATE TABLE Departments (
  department_id INT PRIMARY KEY,
  department_name VARCHAR(50)
);

CREATE TABLE StayRecords (
  record_id INT PRIMARY KEY,
  patient_id INT,
  check_in_date DATE,
  check_out_date DATE
);

CREATE TABLE DepartmentAvgStay (
  department_id INT PRIMARY KEY,
  avg_length_of_stay DECIMAL(10,2)
);

CREATE TABLE StayStatistics (
  stats_id INT PRIMARY KEY,
  department_id INT,
  total_stays INT,
  total_days_decimal DECIMAL(10,2)
);
