tables.table_schema:
CREATE TABLE disease_prevalence (
    disease_id INT PRIMARY KEY,
    disease_name STRING,
    prevalence_rate FLOAT
);

CREATE TABLE populations (
    id SERIAL PRIMARY KEY,
    country VARCHAR(50) NOT NULL,
    population_size INT NOT NULL,
    average_age INT NOT NULL
);

CREATE TABLE risk_factors (
    id SERIAL PRIMARY KEY,
    risk_factor_name VARCHAR(50) NOT NULL,
    description TEXT,
    country_id INT REFERENCES populations(id) ON DELETE CASCADE
);

CREATE TABLE disease_mappings (
    id SERIAL PRIMARY KEY,
    disease_id INT REFERENCES diseases(id) ON DELETE CASCADE,
    risk_factor_id INT REFERENCES risk_factors(id) ON DELETE CASCADE
);
