tables.table_schema:
CREATE TABLE healthcare_resources ( 
	healthcare_resource_id SERIAL PRIMARY KEY, 
	healthcare_resource_name VARCHAR(50) NOT NULL, 
	patient_care_demand INT NOT NULL 
);

CREATE TABLE PatientCareDemands (
  patient_id INT PRIMARY KEY,
  patient_name VARCHAR(50),
  medical_condition VARCHAR(100),
  care_demand INT
);

CREATE TABLE ResourceAllocation (
  resource_id INT PRIMARY KEY,
  resource_name VARCHAR(50),
  resource_type VARCHAR(50),
  availability INT
);

CREATE TABLE PatientCareDemands (
    patient_id INT PRIMARY KEY,
    demand_category VARCHAR(50),
    demand_description TEXT,
    demand_priority INT
);

CREATE TABLE HealthcareProviders (
    provider_id INT PRIMARY KEY,
    provider_name VARCHAR(50),
    specialty VARCHAR(50),
    service_capacity INT
);

CREATE TABLE patients (
    patient_id INT PRIMARY KEY,
    patient_name VARCHAR(50),
    patient_age INT,
    patient_gender VARCHAR(10)
);

CREATE TABLE doctors (
    doctor_id INT PRIMARY KEY,
    doctor_name VARCHAR(50),
    doctor_specialty VARCHAR(50),
    doctor_experience INT
);

CREATE TABLE hospitals (
    hospital_id INT PRIMARY KEY,
    hospital_name VARCHAR(100),
    hospital_location VARCHAR(100),
    hospital_capacity INT
);

CREATE TABLE Hospital (
    hospital_id INT PRIMARY KEY,
    hospital_name VARCHAR(255),
    location VARCHAR(255),
    total_beds INT
);

CREATE TABLE Department (
    department_id INT PRIMARY KEY,
    department_name VARCHAR(255),
    hospital_id INT,
    FOREIGN KEY (hospital_id) REFERENCES Hospital(hospital_id)
);

CREATE TABLE Doctor (
    doctor_id INT PRIMARY KEY,
    doctor_name VARCHAR(255),
    department_id INT,
    FOREIGN KEY (department_id) REFERENCES Department(department_id)
);

CREATE TABLE Patient (
    patient_id INT PRIMARY KEY,
    patient_name VARCHAR(255),
    admission_date DATE,
    discharge_date DATE
);
