tables.table_schema:
CREATE TABLE PatientFeedback (
  feedback_id INT PRIMARY KEY,
  patient_id INT,
  feedback_text TEXT,
  satisfaction_rating INT
);

CREATE TABLE QualityImprovements (
  improvement_id INT PRIMARY KEY,
  improvement_description TEXT,
  feedback_id INT,
  patient_id INT
);

CREATE TABLE CareQualityImprovement {
    improvement_id INT PRIMARY KEY,
    action_taken VARCHAR(255),
    responsible_person VARCHAR(100),
    completion_date DATE
}

CREATE TABLE PatientFeedback (
  patient_id INT PRIMARY KEY,
  feedback_text TEXT,
  satisfaction_rating INT
);

CREATE TABLE PatientSurvey (
  survey_id INT PRIMARY KEY,
  patient_id INT,
  survey_date DATE,
  recommend_to_other_patient BOOLEAN
);

CREATE TABLE CareProviders (
  provider_id INT PRIMARY KEY,
  provider_name TEXT,
  specialty TEXT,
  hospital_id INT
);

CREATE TABLE Hospital (
  hospital_id INT PRIMARY KEY,
  hospital_name TEXT,
  location TEXT
);

CREATE TABLE Department (
  department_id INT PRIMARY KEY,
  department_name TEXT,
  hospital_id INT,
  provider_id INT
);
