tables.table_schema:
CREATE TABLE customer_segments (
    customer_id INT PRIMARY KEY,
    segment_name VARCHAR(255),
    lead_score INT
);

CREATE TABLE customer_segments (
    customer_id INT,
    segment_name VARCHAR(50),
    lead_score INT
);

CREATE TABLE lead_nurturing_strategies (
    strategy_id INT,
    strategy_name VARCHAR(50),
    segment_name VARCHAR(50),
    effectiveness_score INT
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    name VARCHAR(50),
    email VARCHAR(50),
    lead_score INT
);

CREATE TABLE segments (
    segment_id INT PRIMARY KEY,
    segment_name VARCHAR(50),
    description TEXT
);

CREATE TABLE nurturing_strategies (
    strategy_id INT PRIMARY KEY,
    strategy_name VARCHAR(50),
    segment_id INT,
    FOREIGN KEY (segment_id) REFERENCES segments(segment_id)
);

CREATE TABLE CustomerSegments (
 customer_id INT PRIMARY KEY,
 segment_name VARCHAR(255),
 lead_score INT
);

CREATE TABLE LeadScoring (
 lead_id INT PRIMARY KEY,
 lead_name VARCHAR(255),
 lead_score INT
);

CREATE TABLE LeadNurturing (
 nurturing_id INT PRIMARY KEY,
 nurturing_type VARCHAR(255),
 lead_id INT,
 nurturing_date DATE
);

CREATE TABLE CustomerInteractions (
 interaction_id INT PRIMARY KEY,
 customer_id INT,
 interaction_type VARCHAR(255),
 interaction_date DATE
);
