tables.table_schema:
CREATE TABLE stock_prices (
    symbol VARCHAR, 
    price DECIMAL, 
    volume INT, 
    date DATE
);

CREATE TABLE investment_opportunities (
    opportunity_id INT, 
    symbol VARCHAR, 
    opportunity_date DATE, 
    investment_strategy VARCHAR
);

CREATE TABLE stock_prices (
    symbol STRING,
    price DECIMAL,
    date DATE
);

CREATE TABLE stock_trends (
    symbol STRING,
    trend_type STRING,
    trend_score DECIMAL
);

CREATE TABLE stocks (
	stock_id UUID PRIMARY KEY,
	stock_symbol TEXT NOT NULL,
	stock_price DECIMAL,
	stock_date TIMESTAMP
);

CREATE TABLE stock_prices (
	price_id UUID PRIMARY KEY,
	stock_id UUID REFERENCES stocks(stock_id),
	price DECIMAL,
	price_date TIMESTAMP
);

CREATE TABLE stock_trends (
	trend_id UUID PRIMARY KEY,
	stock_id UUID REFERENCES stocks(stock_id),
	trend_type TEXT,
	trend_date TIMESTAMP
);

CREATE TABLE stock_analysis (
	analysis_id UUID PRIMARY KEY,
	stock_id UUID REFERENCES stocks(stock_id),
	analysis_result TEXT,
	analysis_date TIMESTAMP
);

CREATE TABLE stock_recommendations (
	recommendation_id UUID PRIMARY KEY,
	stock_id UUID REFERENCES stocks(stock_id),
	recommendation TEXT,
	recommendation_date TIMESTAMP
);
