tables.table_schema:
CREATE TABLE portfolio_returns (
    date DATE,
    portfolio_id INT,
    return DECIMAL(10, 2),
    risk_level ENUM('low', 'medium', 'high')
);

CREATE TABLE Portfolio (
    id INT PRIMARY KEY,
    investor_name VARCHAR(255),
    portfolio_name VARCHAR(255)
);

CREATE TABLE portfolio_returns (
    stock_symbol VARCHAR PRIMARY KEY,
    return FLOAT,
    date DATE
);

CREATE TABLE risk_metrics (
    metric_name VARCHAR PRIMARY KEY,
    value FLOAT,
    date DATE
);

CREATE TABLE historical_returns (
    stock_symbol VARCHAR,
    return FLOAT,
    date DATE,
    PRIMARY KEY (stock_symbol, date)
);

CREATE TABLE portfolio_returns (
    id SERIAL PRIMARY KEY,
    date DATE,
    return DECIMAL
);

CREATE TABLE risk_evaluation (
    id SERIAL PRIMARY KEY,
    date DATE,
    risk_score DECIMAL
);

CREATE TABLE portfolio_assets (
    id SERIAL PRIMARY KEY,
    portfolio_id INT,
    asset_id INT
);

CREATE TABLE risk_factors (
    id SERIAL PRIMARY KEY,
    factor_name VARCHAR(50),
    factor_weight DECIMAL
);
