tables.table_schema:
CREATE TABLE cash_flows (
  date DATE,
  cash_inflow DECIMAL,
  cash_outflow DECIMAL
);

CREATE TABLE income_statement (
  date DATE,
  revenue DECIMAL,
  expenses DECIMAL
);

CREATE TABLE balance_sheet (
  date DATE,
  assets DECIMAL,
  liabilities DECIMAL
);

CREATE TABLE financial_performance (
  date DATE,
  profitability DECIMAL,
  liquidity DECIMAL
);

CREATE TABLE forecasting (
  date DATE,
  forecasted_cash_flow DECIMAL,
  forecasted_profitability DECIMAL
);
