tables.table_schema:
CREATE TABLE Credit_Risk (
    customer_id INT,
    credit_score INT,
    payment_history TEXT
);

CREATE TABLE credit_scores (
    id INT PRIMARY KEY,
    customer_id INT,
    credit_score INT
);
CREATE TABLE payment_history (
    id INT PRIMARY KEY,
    customer_id INT,
    payment_date DATE,
    amount DECIMAL(10, 2)
);

CREATE TABLE CreditScores (
    id INT PRIMARY KEY,
    customer_id INT,
    credit_score INT
);

CREATE TABLE PaymentHistory (
    id INT PRIMARY KEY,
    customer_id INT,
    payment_date DATE,
    amount DECIMAL
);

CREATE TABLE CreditRiskAnalysis (
    id INT PRIMARY KEY,
    customer_id INT,
    risk_score INT,
    analysis_date DATE
);
