tables.table_schema:
CREATE TABLE EconomicConditions (
    scenario_id INT PRIMARY KEY,
    scenario_name STRING,
    impact_on_financials STRING
);

CREATE TABLE FinancialOutcomes (
    outcome_id INT PRIMARY KEY,
    outcome_name STRING,
    scenario_id INT,
    financial_value DECIMAL
);

CREATE TABLE scenario_analysis (
    id SERIAL PRIMARY KEY,
    scenario_name VARCHAR(100),
    economic_condition VARCHAR(100),
    financial_outcome DECIMAL
);

CREATE TABLE economic_conditions (
    id SERIAL PRIMARY KEY,
    condition_name VARCHAR(100),
    interest_rate DECIMAL,
    inflation_rate DECIMAL
);

CREATE TABLE economic_conditions (
 id INT PRIMARY KEY,
 condition_name STRING
);

CREATE TABLE outcomes (
 id INT PRIMARY KEY,
 scenario_name STRING,
 revenue DECIMAL,
 expenses DECIMAL
);

CREATE TABLE scenarios (
 id INT PRIMARY KEY,
 scenario_name STRING,
 description STRING
);

CREATE TABLE analysis_results (
 id INT PRIMARY KEY,
 scenario_id INT,
 revenue DECIMAL,
 expenses DECIMAL
);
