tables.table_schema:
CREATE TABLE IoT_Data (
    device_id INT PRIMARY KEY,
    timestamp TIMESTAMP NOT NULL,
    temperature DECIMAL NOT NULL,
    humidity DECIMAL NOT NULL
);

CREATE TABLE sensors (
  sensor_id INT PRIMARY KEY,
  sensor_name STRING,
  sensor_type STRING,
  location STRING
);

CREATE TABLE sensor_data (
  data_id INT PRIMARY KEY,
  sensor_id INT, 
  timestamp TIMESTAMP, 
  data_value FLOAT
);

CREATE TABLE devices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(50),
    device_type VARCHAR(50),
    last_update TIMESTAMP
);

CREATE TABLE dashboard_data (
    dashboard_id INT PRIMARY KEY,
    device_id INT REFERENCES devices(device_id),
    dashboard_name VARCHAR(50),
    dashboard_url VARCHAR(100)
);

CREATE TABLE iot_devices (
    device_id INT PRIMARY KEY,
    device_name STRING,
    location STRING,
    data_stream JSON
);

CREATE TABLE data_streams (
    stream_id INT PRIMARY KEY,
    device_id INT REFERENCES iot_devices(device_id),
    stream_data JSON,
    timestamp TIMESTAMP
);

CREATE TABLE dashboard_users (
    user_id INT PRIMARY KEY,
    username STRING,
    email STRING
);

CREATE TABLE user_permissions (
    permission_id INT PRIMARY KEY,
    user_id INT REFERENCES dashboard_users(user_id),
    permission_level INT
);

CREATE TABLE dashboard_visualizations (
    visualization_id INT PRIMARY KEY,
    stream_id INT REFERENCES data_streams(stream_id),
    visualization_type STRING,
    visualization_data JSON
);
