tables.table_schema:
CREATE TABLE reviews (
    review_id INT PRIMARY KEY,
    review_text TEXT,
    review_date TIMESTAMP,
    rating INT
);

CREATE TABLE sentiment_analysis (
    analysis_id INT PRIMARY KEY,
    review_id INT,
    sentiment_score FLOAT,
    sentiment_category VARCHAR(50)
);

CREATE TABLE review_sentiments (
    review_id INT PRIMARY KEY,
    sentiment_label VARCHAR(20),
    sentiment_confidence FLOAT,
    FOREIGN KEY (review_id) REFERENCES customer_reviews(review_id)
);

CREATE TABLE customer_reviews (
    review_id UUID PRIMARY KEY,
    review_text TEXT,
    review_date DATE,
    sentiment_score FLOAT
);

CREATE TABLE review_sentiments (
    sentiment_id UUID PRIMARY KEY,
    review_id UUID,
    sentiment_label TEXT,
    sentiment_probability FLOAT
);

CREATE TABLE product_metadata (
    product_id UUID PRIMARY KEY,
    product_name TEXT,
    category TEXT,
    brand TEXT
);

CREATE TABLE customer_demographics (
    customer_id UUID PRIMARY KEY,
    age INT,
    gender TEXT,
    location TEXT
);

CREATE TABLE customer_reviews (
    review_id SERIAL PRIMARY KEY,
    customer_id INT,
    review_text TEXT,
    review_date DATE
);

CREATE TABLE customer_sentiments (
    sentiment_id SERIAL PRIMARY KEY,
    review_id INT,
    sentiment_score FLOAT,
    sentiment_category VARCHAR(50)
);

CREATE TABLE product_reviews (
    product_id SERIAL PRIMARY KEY,
    product_name VARCHAR(100),
    review_text TEXT,
    review_date DATE
);

CREATE TABLE customer_preferences (
    customer_id INT,
    preference_id SERIAL PRIMARY KEY,
    preference_name VARCHAR(50)
);

CREATE TABLE customer_feedback (
    feedback_id SERIAL PRIMARY KEY,
    customer_id INT,
    feedback_text TEXT,
    feedback_date DATE
);
