tables.table_schema:
CREATE TABLE data_cleansing (
    id INT PRIMARY KEY,
    raw_data VARCHAR(255),
    cleansed_data VARCHAR(255)
);

CREATE TABLE transformation (
    id INT PRIMARY KEY,
    original_data VARCHAR(255),
    transformed_data VARCHAR(255)
);

CREATE TABLE data_cleansing (
    id INT PRIMARY KEY,
    data VARCHAR(255),
    cleansing_trigger VARCHAR(255) DEFAULT 'auto',
    last_cleansed TIMESTAMP
);

CREATE TABLE logs (
    log_id INT PRIMARY KEY,
    log_message TEXT,
    log_type VARCHAR(50),
    timestamp TIMESTAMP
);

CREATE TABLE table1 (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    age INT
);

CREATE TABLE table2 (
    id INT PRIMARY KEY,
    date_of_birth DATE,
    gender VARCHAR(10)
);

CREATE TABLE table3 (
    id INT PRIMARY KEY,
    address VARCHAR(255),
    city VARCHAR(50),
    state VARCHAR(50)
);

CREATE TABLE table4 (
    id INT PRIMARY KEY,
    amount DECIMAL(10, 2),
    category VARCHAR(50)
);

CREATE TABLE raw_data_table (
    id INT PRIMARY KEY,
    data VARCHAR(255),
    timestamp TIMESTAMP
);

CREATE TABLE business_rules_table (
    rule_id INT PRIMARY KEY,
    rule_name VARCHAR(255),
    rule_description TEXT
);

CREATE TABLE cleansing_logs_table (
    log_id INT PRIMARY KEY,
    cleansing_action VARCHAR(255),
    timestamp TIMESTAMP
);

CREATE TABLE transformed_data_table (
    id INT PRIMARY KEY,
    transformed_data VARCHAR(255),
    transformation_timestamp TIMESTAMP
);

CREATE TABLE data_pipeline_status_table (
    pipeline_id INT PRIMARY KEY,
    pipeline_name VARCHAR(255),
    status VARCHAR(50),
    completion_timestamp TIMESTAMP
);
