tables.table_schema:
CREATE TABLE BrandSentimentAnalysis (
    id UUID PRIMARY KEY,
    brand_name VARCHAR(255) NOT NULL,
    sentiment_score FLOAT NOT NULL,
    engagement_level INTEGER
);

CREATE TABLE BrandSentiment (
    brand_id INT PRIMARY KEY,
    sentiment_score FLOAT,
    engagement_level INT
);

CREATE TABLE users (
	user_id INT PRIMARY KEY,
	username VARCHAR(50),
	email VARCHAR(100),
	created_at TIMESTAMP
);

CREATE TABLE brand_sentiment_analysis (
    brand_id UUID PRIMARY KEY,
    brand_name TEXT NOT NULL,
    sentiment_score FLOAT NOT NULL,
    engagement_level INT
);

CREATE TABLE user_profiles (
    user_id UUID PRIMARY KEY,
    user_name TEXT NOT NULL,
    user_location TEXT,
    user_followers INT
);

CREATE TABLE brand_engagement_analysis (
    brand_id UUID PRIMARY KEY,
    engagement_score FLOAT NOT NULL,
    engagement_type TEXT,
    engagement_timestamp TIMESTAMP
);

CREATE TABLE analytics_dashboard (
    dashboard_id UUID PRIMARY KEY,
    dashboard_name TEXT NOT NULL,
    dashboard_owner TEXT,
    dashboard_last_updated TIMESTAMP
);
