tables.table_schema:
CREATE TABLE moisture_sensors (
  sensor_id SERIAL PRIMARY KEY,
  location VARCHAR(255),
  soil_moisture_level FLOAT,
  timestamp TIMESTAMP
);

CREATE TABLE MoistureSensorData (
  sensor_id UUID PRIMARY KEY,
  location TEXT,
  moisture_level DECIMAL,
  timestamp TIMESTAMP
);

CREATE TABLE SensorStatus (
  status_id UUID PRIMARY KEY,
  sensor_id UUID,
  status STRING,
  last_update TIMESTAMP
);
